glsl3.vert:
precision highp float;
uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  mediump mat4 cc_matViewInv;
  mediump mat4 cc_matProj;
  mediump mat4 cc_matProjInv;
  mediump mat4 cc_matViewProj;
  mediump mat4 cc_matViewProjInv;
  mediump vec4 cc_cameraPos;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in lowp vec4 a_color;
in mediump vec2 a_uv0;
out mediump vec2 v_uv0;
out lowp vec4 v_color;
void main () {
  mat4 mvp;
  mvp = cc_matViewProj;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
uniform sampler2D texture;
uniform sampler2D texture2;
in mediump vec2 v_uv0;
uniform Time {
  float time;
  float ratio;
};
in lowp vec4 v_color;
vec4 getFromColor(vec2 uv) {
  return texture2D(texture, uv);
}
vec4 getToColor(vec2 uv) {
  return texture2D(texture2, uv);
}
uniform Transition {
  float steps;
  float horizontalHexagons;
};
struct Hexagon {
  float q;
  float r;
  float s;
};
Hexagon createHexagon(float q, float r){
  Hexagon hex;
  hex.q = q;
  hex.r = r;
  hex.s = -q - r;
  return hex;
}
Hexagon roundHexagon(Hexagon hex){
  float q = floor(hex.q + 0.5);
  float r = floor(hex.r + 0.5);
  float s = floor(hex.s + 0.5);
  float deltaQ = abs(q - hex.q);
  float deltaR = abs(r - hex.r);
  float deltaS = abs(s - hex.s);
  if (deltaQ > deltaR && deltaQ > deltaS)
    q = -r - s;
  else if (deltaR > deltaS)
    r = -q - s;
  else
    s = -q - r;
  return createHexagon(q, r);
}
Hexagon hexagonFromPoint(vec2 point, float size) {
  point.y /= ratio;
  point = (point - 0.5) / size;
  float q = (sqrt(3.0) / 3.0) * point.x + (-1.0 / 3.0) * point.y;
  float r = 0.0 * point.x + 2.0 / 3.0 * point.y;
  Hexagon hex = createHexagon(q, r);
  return roundHexagon(hex);
}
vec2 pointFromHexagon(Hexagon hex, float size) {
  float x = (sqrt(3.0) * hex.q + (sqrt(3.0) / 2.0) * hex.r) * size + 0.5;
  float y = (0.0 * hex.q + (3.0 / 2.0) * hex.r) * size + 0.5;
  return vec2(x, y * ratio);
}
vec4 transition (vec2 uv) {
  float dist = 2.0 * min(time, 1.0 - time);
  dist = steps > 0.0 ? ceil(dist * steps) / steps : dist;
  float size = (sqrt(3.0) / 3.0) * dist / horizontalHexagons;
  vec2 point = dist > 0.0 ? pointFromHexagon(hexagonFromPoint(uv, size), size) : uv;
  return mix(getFromColor(point), getToColor(point), time);
}
void main () {
  gl_FragColor = v_color * transition(v_uv0);
}

