glsl3.vert:
precision highp float;
uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  mediump mat4 cc_matViewInv;
  mediump mat4 cc_matProj;
  mediump mat4 cc_matProjInv;
  mediump mat4 cc_matViewProj;
  mediump mat4 cc_matViewProjInv;
  mediump vec4 cc_cameraPos;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in lowp vec4 a_color;
in mediump vec2 a_uv0;
out mediump vec2 v_uv0;
out lowp vec4 v_color;
void main () {
  mat4 mvp;
  mvp = cc_matViewProj;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
uniform sampler2D texture;
uniform sampler2D texture2;
in mediump vec2 v_uv0;
uniform Time {
  float time;
  float ratio;
};
in lowp vec4 v_color;
vec4 getFromColor(vec2 uv) {
  return texture(texture, uv);
}
vec4 getToColor(vec2 uv) {
  return texture(texture2, uv);
}
float progress = time;
uniform Transition {
  float direction;
  float l_threshold;
  float above;
};
float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}
vec2 mod289(vec2 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}
vec3 permute(vec3 x) {
  return mod289(((x*34.0)+1.0)*x);
}
float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,
                      0.366025403784439,
                     -0.577350269189626,
                      0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod289(i);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
    + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}
float luminance(vec4 color){
  return color.r*0.299+color.g*0.587+color.b*0.114;
}
vec2 center = vec2(1.0, direction);
vec4 transition(vec2 uv) {
  vec2 p = uv.xy / vec2(1.0).xy;
  if (progress == 0.0) {
    return getFromColor(p);
  } else if (progress == 1.0) {
    return getToColor(p);
  } else {
    float x = progress;
    float dist = distance(center, p)- progress*exp(snoise(vec2(p.x, 0.0)));
    float r = x - rand(vec2(p.x, 0.1));
    float m;
    if(above > 0.0){
     m = dist <= r && luminance(getFromColor(p))>l_threshold ? 1.0 : (progress*progress*progress);
    }
    else{
     m = dist <= r && luminance(getFromColor(p))<l_threshold ? 1.0 : (progress*progress*progress);
    }
    return mix(getFromColor(p), getToColor(p), m);
  }
}
void main () {
  gl_FragColor = v_color * transition(v_uv0);
}

