glsl3.vert:
precision highp float;
uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  mediump mat4 cc_matViewInv;
  mediump mat4 cc_matProj;
  mediump mat4 cc_matProjInv;
  mediump mat4 cc_matViewProj;
  mediump mat4 cc_matViewProjInv;
  mediump vec4 cc_cameraPos;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  v_uv0 = a_uv0;
  gl_Position = vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
uniform sampler2D texture;
void main () {
  gl_FragColor = texture(texture, v_uv0);
}

glsl1.vert:
precision highp float;
attribute vec3 a_position;
attribute vec2 a_uv0;
varying vec2 v_uv0;
void main () {
  v_uv0 = a_uv0;
  gl_Position = vec4(a_position, 1);
}

glsl1.frag:
precision highp float;
varying vec2 v_uv0;
uniform sampler2D texture;
void main () {
  gl_FragColor = texture2D(texture, v_uv0);
}
